for file in atari++_1.*gz ; do echo "$file"; ( cd atari++ ; git rm -rf . ); tar xvf $file ; ( cd atari++ ; chmod a-x *.cpp *.h* *.v*; git add . ; d="$(cat README.History  |head -n2 | tail -n1 | cut -d' ' -f5,6)"; c="$(tail -n +$(( "$(grep -n '^Atari' README.History  | tail -n1 | sed 's=:.*==g')"+1)) < README.History  | sed 's:^\t::g' | sed 's:^\([^- ]\):  \1:g')"; GIT_COMMITTER_EMAIL=@ GIT_COMMITTER_NAME="Thomas Richter" GIT_COMMITTER_DATE="$d" git commit -a --date "$d" -m "$(cat README.History  | grep '^Atari' | tail -n1)" -m"$c" --author "Thomas Richter<@>" ) ; done
